    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        if (jTextField1.getText().trim().length() > 0 && jTextField2.getText().trim().length() > 0 && jTextField3.getText().trim().length() > 0) {
            almacen.modificarProveedor(jTextField2.getText().trim(), jTextField3.getText().trim(), Integer.parseInt(jTextField1.getText().trim()));
            JOptionPane.showMessageDialog(this, "modificacion realizada", "proveedor", JOptionPane.INFORMATION_MESSAGE);
        } else {
            JOptionPane.showMessageDialog(this, "modificacion rechazada", "proveedor", JOptionPane.INFORMATION_MESSAGE);
        }
    }//GEN-LAST:event_jButton1ActionPerformed

