    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
            if (jTextField1.getText().trim().length() > 0 && jTextField2.getText().trim().length() > 0 && jTextField3.getText().trim().length() > 0) {
                almacen.insertarProducto(jTextField1.getText().trim(), Integer.parseInt(jTextField2.getText().trim()), Integer.parseInt(jTextField3.getText().trim()));
                JOptionPane.showMessageDialog(this, "Insercion realizada", "producto", JOptionPane.INFORMATION_MESSAGE);
            } else {
                JOptionPane.showMessageDialog(this, "Insercion rechazada", "producto", JOptionPane.INFORMATION_MESSAGE);
            }
    }//GEN-LAST:event_jButton1ActionPerformed

