    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

        if (jTextField1.getText().trim().length() > 0 && jTextField2.getText().trim().length() > 0) {
            almacen.insertarProveedor(jTextField1.getText().trim(), jTextField2.getText().trim());
            JOptionPane.showMessageDialog(this, "Insercion realizada", "localizacion", JOptionPane.INFORMATION_MESSAGE);
        } else {
            JOptionPane.showMessageDialog(this, "Insercion rechazada", "localizacion", JOptionPane.INFORMATION_MESSAGE);
        }

    }//GEN-LAST:event_jButton1ActionPerformed

